CREATE PROCEDURE SP_CapNhatGiangVien
(
	@MaGiangVien  NVARCHAR(20),
	@HoDem  NVARCHAR(50),
	@Ten  NVARCHAR(20),
	@GioiTinh BIT,
	@NgaySinh VARCHAR(10),
	@HocVi NVARCHAR(50),
	@IDKhoa INT,
	@CCCD VARCHAR(20),
	@SoDienThoai VARCHAR(20),
	@DiaChiLienLac NVARCHAR(200),
	@IsChamDutHD BIT  
)
AS
BEGIN
	IF NOT EXISTS (SELECT gv.Id FROM dbo.GiangVien gv WHERE gv.MaGiangVien = @MaGiangVien)
	BEGIN
		INSERT INTO dbo.GiangVien
		VALUES
		(   @MaGiangVien,
			@HoDem,
			@Ten,
			@GioiTinh,
			@NgaySinh,
			@HocVi,
			@IDKhoa,
			@CCCD,
			@SoDienThoai,
			@DiaChiLienLac,
			@IsChamDutHD 
			)
	END 
	ELSE
    BEGIN
        UPDATE dbo.GiangVien 
		SET HoDem = @HoDem,
			Ten = @Ten,
			GioiTinh = @GioiTinh,
			NgaySinh = @NgaySinh,
			HocVi = @HocVi,
			IDKhoa = @IDKhoa,
			CCCD = @CCCD,
			SoDienThoai = @SoDienThoai,
			DiaChiLienLac = @DiaChiLienLac,
			IsChamDutHD  = @IsChamDutHD 
		WHERE MaGiangVien = @MaGiangVien
    END
END
GO

